function updateGauges()
  hp = hp or 1
  gp = gp or 1
  xp = xp or 1
	max_hp = max_hp or 1
	max_gp = max_gp or 1
	max_xp = max_xp or 1

	hpbar:setValue(cap(hp, max_hp), max_hp, "<p style='font-size:12pt'><b>" .. (hp or "?") .. " HP</b></p>")
	gpbar:setValue(cap(gp, max_gp), max_gp, "<p style='font-size:12pt'><b>" .. (gp or "?") .. " GP</b></p>")
	local xp_bar_max = max_xp
	if xp_helper or xp_health_pause then
		xp_bar_max = xp_goal
	end

	local rate = ""
	if xp_stats and xp_stats.xp_per_hour() > 0 then
		local time_left = xp_stats.time_to(xp_goal)
		if time_left then
			time_left = " " .. time_left .. " kvar"
		else
			time_left = ""
		end
		rate = xp_stats.presentation() .. time_left
	end
	xpbar:setValue(cap(xp, xp_bar_max), xp_bar_max, "<p style='font-size:12pt'><b>" .. (math.floor(xp/1000) or "?") .. "k XP ".. rate .. "</b></p>")

  setGauge("lockout", lockout, max_lockout)
end

function cap(x, max)
	if not max then
		return 0
	end
	if not x then
		return 0
	end
	if x > max then
		return max
	end
	return x
end

button_width = 120
button_height = 30

function setupButtons()
	top_container = Geyser.Container:new({
	  name = "top_container",
	  x="45%", y="4px",
	  width = "100%", height="100%",
	})
--	top_container:flash()

	auto_xp_label = Geyser.Label:new({
		name = "auto_xp_label",
		message = [[<p style="font-size:14pt">Auto-XP</p>]],
		x = 0, y = 0, width = button_width, height = button_height
	}, top_container)
	auto_xp_label:setClickCallback("auto_xp_click")

	select_xp_label = Geyser.Label:new({
		name = "select_xp_label",
		message = [[<p style="font-size:14pt">XP-Area</p>]],
		x = 0, y = button_height, width = button_width, height = button_height
	}, top_container)
	select_xp_label:setClickCallback("select_xp_click")

	auto_cast_label = Geyser.Label:new({
		name = "auto_cast_label",
		message = [[<p style="font-size:14pt">Auto-Offense</p>]],
		x = 2*button_width, y = 0, width = button_width, height = button_height
	}, top_container)
	auto_cast_label:setClickCallback("auto_cast_click")

	refreshButtons()
end

function setupAbilityButtons()
  if is_taniwha then
    setupTaniwhaButtons()
  elseif is_bard then
    setupBardButtons()
  end
  refreshButtons()
end

function addBuffButton(name, title, action, count)
  local label = Geyser.Label:new({
		name = name,
		message = [[<p style="font-size:14pt">]] .. title .. [[</p>]],
		x = button_width, y = count*button_height, width = button_width, height = button_height
	}, top_container)
	label:setClickCallback(action)
  return label
end

function addAbilityButton(name, count, label, callback)
	local label = Geyser.Label:new({
		name = name,
		message = "<p style='font-size:14pt'>".. label .. "</p>",
		x = 0, y = 30*count, width = 120, height = 30
	}, ab_container)
	label:setClickCallback(callback)

	local neutral_style = [[
		background-color: DarkRed;
		padding: 2px;
		margin: 2px 0 0 2px;
		border-radius: 4px;
	]]
	label:setStyleSheet(neutral_style)

	return label
end

	neutral_style = [[
    font-size: 14pt;
		background-color: grey;
		padding: 2px;
		margin: 2px 0 0 2px;
		border-radius: 4px;
	]]

	active_style = [[
		background-color: green;
		padding: 2px;
		margin: 2px 0 0 2px;
		border-radius: 4px;
	]]

	paused_style = [[
		background-color: GoldenRod;
		padding: 2px;
		margin: 2px 0 0 2px;
		border-radius: 4px;
	]]

  inactive_trying_style = [[
		background-color: grey;
		padding: 0px;
		margin: 2px 0 0 2px;
    border: 2px solid green;
		border-radius: 4px;
	]]

  active_not_trying_style = [[
		background-color: green;
		padding: 0px;
		margin: 2px 0 0 2px;
    border: 2px solid grey;
		border-radius: 4px;
	]]

function refreshButtons()

	local select_xp_style = neutral_style
	if xp_area then
		select_xp_style = active_style
	end
	select_xp_label:setStyleSheet(select_xp_style)

	local xp_style = neutral_style
	if xp_helper then
		xp_style = active_style
	elseif xp_health_pause then
		xp_style = paused_style
	end
	auto_xp_label:setStyleSheet(xp_style)

	updateStyle(auto_buff_label, auto_buff)
	updateStyle(auto_cast_label, auto_cast)

  if is_taniwha then
    refreshTaniwhaButtons()
  elseif is_bard then
    refreshBardButtons()
  end

end

function updateStyleWithTrying(label, active, trying)
  local style = neutral_style
  if active and not trying then
    style = active_not_trying_style
  elseif not active and trying then
    style = inactive_trying_style
  elseif active then
    style = active_style
  end

  label:setStyleSheet(style)
end

function updateStyle(label, cond)
	if not label then
		return
	end
	local style = neutral_style
	if cond then
		style = active_style
	end
	label:setStyleSheet(style)
end

