Script.script:
function setupChat()
	chat_window = Geyser.MiniConsole:new({
	  name="Chat1",
	  x="70%", y="81%",
	  width="29%", height="20%",
	})
	setMiniConsoleFontSize("Chat1", 12)
	setBackgroundColor("Chat1", 0,0,0,255)
end

locations = {

guild = 314,
kk_pub = 69,
kk_xp = 653,
newb_vault = 352,
mirkwood = 957

}


function showLocations()
	display(locations)
end

function showRoomId(...)
	echo("\n")
	for i = 3,arg['n'] do
		local room_id = arg[i]
		echo(room_id .. "\n")
   end
end

function highlightRooms(rooms) local r,g,b = unpack(color_table.green) local br,bg,bb = unpack(color_table.yellow)
for room_id, active in pairs(rooms) do highlightRoom(room_id, r,g,b,br,bg,bb, 2, 0, 255) end end
function unhighlightRooms(rooms) for room_id, active in pairs(rooms) do unHighlightRoom(room_id) end end

function loadXpData()
	xp_areas = {}
	table.load(getMudletHomeDir() .. "/xp_areas.dat", xp_areas)
end

function saveXpData()
	xp_areas = xp_areas or {}
	table.save(getMudletHomeDir() .. "/xp_areas.dat", xp_areas)
end

function listXpAreas()

	xp_areas = xp_areas or {}

	echo("\n")
	for key, value in pairs(xp_areas) do
		echo("  " .. key .. " (" .. table.size(value.rooms) .. " rooms)\n")
	end
	echo("\n")
end

function setXpWaitingRoom(name, label, room_id)
	if not xp_area then
		echo("No XP area defined\n")
		return
	end

	echo("\n")
	echo(getRoomName(room_id) .. "\n")
	xp_area.waiting_room = room_id
end

